mapper.update:
UPDATE medince set medinceName=#{medinceName}, buyNumber=#{buyNumber},restNumber=#{restNumber},
functional=#{functional},price=#{price}
where  medinceId=#{medinceId}

UPDATE buyer
integral=#{integral}, cumoney=#{cumoney}, sunmoney=#{sunmoney},
where userId=#{userId}

UPDATE redemption
award=#{award}, number=#{number},
WHERE id=#{id}
